import React, { useState } from "react";
import * as Components from "./Components";
import { useNavigate } from "react-router-dom";

function SubmitRegistration(e, toggle, step, setStep) {
  e.preventDefault();
  const formEle = e.target;

  if (step < 3) {
    setStep(step + 1);
    return;
  }

  const name = formEle.name.value.trim();
  const email = formEle.email.value.trim();
  const password = formEle.password.value;

  if (!name || name.length < 3) {
    alert("Name must be at least 3 characters long.");
    return;
  }

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!email || !emailRegex.test(email)) {
    alert("Please enter a valid email address.");
    return;
  }

  const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[^\w\s])[^\s]{8,}$/;
  if (!password || !passwordRegex.test(password)) {
    alert(
      "Password must be at least 8 characters long and include uppercase, lowercase, number, and special character."
    );
    return;
  }

  fetch("https://script.google.com/macros/s/YOUR_SCRIPT_URL/exec", {
    method: "POST",
    headers: { "Content-Type": "application/x-www-form-urlencoded" },
    body: new URLSearchParams({ action: "registration", name, email, password }),
  })
    .then((res) => res.json())
    .then((data) => {
      if (data.status === "success") {
        alert(data.message);
        formEle.reset();
        toggle(true);
      } else {
        alert(`Error: ${data.message}`);
      }
    })
    .catch((error) => {
      console.error(error);
      alert("Oops! Something went wrong. Please try again later.");
    });
}

function signInVerification(e, navigate) {
  e.preventDefault();
  const formEle = e.target;
  const email = formEle.email.value.trim();
  const password = formEle.password.value;

  if (!email || !password) {
    alert("Please enter both email and password.");
    return;
  }

  fetch("https://script.google.com/macros/s/YOUR_SCRIPT_URL/exec", {
    method: "POST",
    headers: { "Content-Type": "application/x-www-form-urlencoded" },
    body: new URLSearchParams({ action: "signin", email, password }),
  })
    .then((res) => res.json())
    .then((data) => {
      if (data.status === "success") {
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        alert("Invalid email or password.");
      }
    })
    .catch((error) => {
      console.error(error);
      alert("Oops! Something went wrong. Please try again later.");
    });
}

function Login() {
  const [signIn, toggle] = useState(true);
  const [step, setStep] = useState(1);
  const navigate = useNavigate();

  return (
    <Components.Container>
      <Components.SignUpContainer signinIn={signIn}>
        <Components.Form onSubmit={(e) => SubmitRegistration(e, toggle, step, setStep)}>
          <Components.Title>Sign Up - Step {step} of 3</Components.Title>
          {step === 1 && <Components.Input type="text" name="name" placeholder="Name" />}
          {step === 2 && <Components.Input type="email" name="email" placeholder="Email" />}
          {step === 3 && <Components.Input type="password" name="password" placeholder="Password" />}
          <Components.ProgressBar>
            <Components.Progress step={step} />
          </Components.ProgressBar>
          <Components.Button type="submit">{step < 3 ? "Next" : "Sign Up"}</Components.Button>
        </Components.Form>
      </Components.SignUpContainer>

      <Components.SignInContainer signinIn={signIn}>
        <Components.Form onSubmit={(e) => signInVerification(e, navigate)}>
          <Components.Title>Sign in</Components.Title>
          <Components.Input type="email" name="email" placeholder="Email" />
          <Components.Input type="password" name="password" placeholder="Password" />
          <Components.Anchor href="#">Forgot your password?</Components.Anchor>
          <Components.Button type="submit">Sign In</Components.Button>
        </Components.Form>
      </Components.SignInContainer>
    </Components.Container>
  );
}

export default Login;
