// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import DashboardAdmin from "./pages/DashboardAdmin";
import UsersManagement from "./pages/UsersManagement";
import Reports from "./pages/Reports";
import Settings from "./pages/Settings";
import DashboardUser from "./pages/DashboardUser"; // Halaman dashboard untuk user
import { AuthProvider, useAuth } from "./context/AuthContext";

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <div style={{ display: "flex" }}>
          <Sidebar />
          <div style={{ flex: 1 }}>
            <Navbar />
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/dashboard" element={<PrivateRoute role="admin" component={DashboardAdmin} />} />
              <Route path="/users" element={<PrivateRoute role="admin" component={UsersManagement} />} />
              <Route path="/reports" element={<PrivateRoute role="admin" component={Reports} />} />
              <Route path="/settings" element={<PrivateRoute role="admin" component={Settings} />} />
              <Route path="/user-dashboard" element={<PrivateRoute role="user" component={DashboardUser} />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
};

const PrivateRoute = ({ role, component: Component }) => {
  const { user } = useAuth();

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (user.role !== role) {
    return <Navigate to="/" />;
  }

  return <Component />;
};

const Login = () => {
  const { login } = useAuth();

  const handleLogin = (role) => {
    login(role); // Simulasikan login dengan role "admin" atau "user"
  };

  return (
    <div>
      <h2>Login</h2>
      <button onClick={() => handleLogin("admin")}>Login as Admin</button>
      <button onClick={() => handleLogin("user")}>Login as User</button>
    </div>
  );
};

export default App;
