import React, { useState, useEffect } from "react";
import * as Components from "../Components";
import { useNavigate } from "react-router-dom";

function Login() {
  const [signIn, toggle] = useState(true);
  const [step, setStep] = useState(1);
  const [progress, setProgress] = useState(33);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    laptopName: "",
    handphoneName: "",
    documents: [],
  });

  const navigate = useNavigate();
  const [previewURLs, setPreviewURLs] = useState([]);

  // Update progress bar secara otomatis saat step berubah
  useEffect(() => {
    setProgress((step / 3) * 100);
  }, [step]);

  // Generate URL preview
  useEffect(() => {
    const urls = formData.documents.map(file => file ? URL.createObjectURL(file) : null);
    setPreviewURLs(urls);

    return () => urls.forEach(url => url && URL.revokeObjectURL(url));
  }, [formData.documents]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileUpload = (e, index) => {
    const file = e.target.files[0];
    if (file) {
      setFormData((prevState) => {
        const updatedDocuments = [...prevState.documents];
        updatedDocuments[index] = file;
        return { ...prevState, documents: updatedDocuments };
      });
    }
  };

  const handleDrop = (e, index) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) {
      setFormData((prevState) => {
        const updatedDocuments = [...prevState.documents];
        updatedDocuments[index] = file;
        return { ...prevState, documents: updatedDocuments };
      });
    }
  };

  const removeFile = (index) => {
    setFormData((prevState) => ({
      ...prevState,
      documents: prevState.documents.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (step === 3) {
      SubmitRegistration(formData, toggle);
    } else {
      setStep((prevStep) => prevStep + 1);
    }
  };

  const handlePrevious = () => {
    if (step > 1) setStep((prevStep) => prevStep - 1);
  };

  const SubmitRegistration = (data, toggle) => {
    console.log("Submitting:", data);
    alert("Registration Successful!");
    toggle(true);
  };

  function signInVerification(e) {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;

    if (email && password) {
      alert("Successfully signed in!");
      navigate("/home");
    } else {
      alert("Please enter both email and password.");
    }
  }

  return (
    <Components.Container>
      <Components.SignUpContainer signinIn={signIn}>
        <Components.Form onSubmit={handleSubmit}>
          <Components.Title>Create Account</Components.Title>

          {step === 1 && (
            <>
              <Components.Input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </>
          )}

          {step === 2 && (
            <>
              <Components.Input
                type="text"
                name="laptopName"
                placeholder="Laptop Name"
                value={formData.laptopName}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="text"
                name="handphoneName"
                placeholder="Handphone Name"
                value={formData.handphoneName}
                onChange={handleInputChange}
                required
              />
            </>
          )}

          {step === 3 && (
            <div style={{ textAlign: "center" }}>
              <p>Drag and drop documents here, or click to upload</p>
              <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gap: "10px" }}>
                {[0, 1, 2, 3].map((index) => (
                  <div
                    key={index}
                    style={{
                      border: formData.documents?.[index] ? "none" : "2px dashed #ccc",
                      width: "80px",
                      height: "80px",
                      position: "relative",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      overflow: "hidden",
                      borderRadius: "8px",
                    }}
                    onDrop={(e) => handleDrop(e, index)}
                    onDragOver={(e) => e.preventDefault()}
                    onClick={() => document.getElementById(`file-upload-${index}`).click()}
                  >
                    {formData.documents?.[index] ? (
                      <div style={{ width: "100%", height: "100%" }}>
                        <div
                          onClick={(e) => {
                            e.stopPropagation();
                            removeFile(index);
                          }}
                          style={{
                            position: "absolute",
                            top: "5px",
                            right: "5px",
                            fontSize: "14px",
                            color: "red",
                            cursor: "pointer",
                            background: "white",
                            borderRadius: "50%",
                            padding: "3px",
                          }}
                        >
                          Ã—
                        </div>
                        <img
                          src={URL.createObjectURL(formData.documents[index])}
                          alt="Preview"
                          style={{ width: "100%", height: "100%", objectFit: "cover" }}
                        />
                      </div>
                    ) : (
                      <span style={{ fontSize: "24px", color: "#ccc" }}>+</span>
                    )}
                  </div>
                ))}
              </div>
              {[0, 1, 2, 3].map((index) => (
                <input
                  key={index}
                  type="file"
                  onChange={(e) => handleFileUpload(e, index)}
                  style={{ display: "none" }}
                  id={`file-upload-${index}`}
                />
              ))}
            </div>
          )}

          <div style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}>
            {step > 1 && <Components.Button type="button" onClick={handlePrevious}>Previous</Components.Button>}
            <Components.Button type="submit" style={{ marginLeft: "10px" }}>{step === 3 ? "Submit" : "Next"}</Components.Button>
          </div>

          <div style={{ marginTop: "20px", height: "10px", backgroundColor: "#e0e0e0", borderRadius: "5px" }}>
            <div style={{ width: `${progress}%`, height: "10px", backgroundColor: "#76c7c0", borderRadius: "5px", transition: "width 0.3s ease" }}></div>
          </div>
        </Components.Form>
      </Components.SignUpContainer>
    </Components.Container>
  );
}

export default Login;
