 import React from "react";
 import * as Components from './Components';

 function SubmitRegistration(e) {
    e.preventDefault();
  
    const formEle = e.target;
    const name = formEle.name.value.trim();
    const email = formEle.email.value.trim();
    const password = formEle.password.value;
  
    // Validasi data di sisi klien
    if (!name || name.length < 3) {
      alert("Name must be at least 3 characters long.");
      return;
    }
  
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email || !emailRegex.test(email)) {
      alert("Please enter a valid email address.");
      return;
    }
  
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[^\w\s])[^\s]{8,}$/;
    if (!password || !passwordRegex.test(password)) {
      alert(
        "Password must be at least 8 characters long and include uppercase, lowercase, number, and special character."
      );
      return;
    }
  
    // Kirim data ke Google Apps Script
    const formData = new FormData(formEle);
  
    fetch(
      "https://script.google.com/macros/s/AKfycbwsZuBhPyb51xmXWTFIOE7SenHWCfNqwqn8MKv_8ghFYzCxuQmmbptpFgc8knf5Bo5eaQ/exec",
      {
        method: "POST",
        body: formData,
      }
    )
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! Status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        if (data.status === "success") {
          alert(data.message); // Pesan sukses dari server
          setS
        } else {
          alert(`Error: ${data.message}`); // Pesan error dari server
        }
      })
      .catch((error) => {
        console.error(error);
        alert(
          "Oops! Something went wrong. Please try again later or contact support."
        );
      });
  }
  
  

 function App() {
    const [signIn, toggle] = React.useState(true);

      return(
          <Components.Container>
              <Components.SignUpContainer signinIn={signIn}>
                  <Components.Form onSubmit={(e)=>SubmitRegistration(e)}>
                      <Components.Title>Create Account</Components.Title>
                      <Components.Input type='text' name='name' placeholder='Name'/>
                      <Components.Input type='email' name='email' placeholder='Email' />
                      <Components.Input type='password' name="password" placeholder='Password'/>
                      <Components.Button type="submit">Sign Up</Components.Button>
                  </Components.Form>
              </Components.SignUpContainer>

              <Components.SignInContainer signinIn={signIn}>
                   <Components.Form>
                       <Components.Title>Sign in</Components.Title>
                       <Components.Input type='email' placeholder='Email' />
                       <Components.Input type='password' placeholder='Password' />
                       <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                       <Components.Button>Sign In</Components.Button>
                   </Components.Form>
              </Components.SignInContainer>

              <Components.OverlayContainer signinIn={signIn}>
                  <Components.Overlay signinIn={signIn}>

                    <Components.LeftOverlayPanel signinIn={signIn}>
                      <Components.Title>Welcome Back!</Components.Title>
                      <Components.Paragraph>
                          To keep connected with us please login with your personal info
                      </Components.Paragraph>
                      <Components.GhostButton onClick={() => toggle(true)}>
                          Sign In
                      </Components.GhostButton>
                    </Components.LeftOverlayPanel>

                    <Components.RightOverlayPanel signinIn={signIn}>
                        <Components.Title>Hello, Friend!</Components.Title>
                        <Components.Paragraph>
                            Enter Your personal details and start journey with us
                        </Components.Paragraph>
                            <Components.GhostButton onClick={() => toggle(false)}>
                                Sigin Up
                            </Components.GhostButton> 
                    </Components.RightOverlayPanel>
  
                  </Components.Overlay>
              </Components.OverlayContainer>

          </Components.Container>
      )
 }

 export default App;
