import React, { useState } from "react";
import * as Components from "../Components";
import { useNavigate } from "react-router-dom";

function Login() {
  const [signIn, toggle] = useState(true);
  const [step, setStep] = useState(1); // Track current step
  const [progress, setProgress] = useState(33); // Track progress percentage
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    laptopName: "",
    handphoneName: "",
    documents: null,
  });
  const navigate = useNavigate();

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle file upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, documents: file });
  };

  // Handle drag-and-drop file upload
  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    setFormData({ ...formData, documents: file });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    if (step === 3) {
      // Final submission
      SubmitRegistration(formData, toggle);
    } else {
      // Move to the next step
      setStep(step + 1);
      setProgress(((step + 1) / 3) * 100);
    }
  };

  // Handle "Previous" button click
  const handlePrevious = () => {
    if (step > 1) {
      setStep(step - 1);
      setProgress(((step - 1) / 3) * 100);
    }
  };

  function signInVerification(e, navigate) {
    e.preventDefault();
  
    // You can replace this logic with your actual sign-in verification logic
    const email = e.target.email.value;
    const password = e.target.password.value;
  
    if (email && password) {
      // Simulate a successful sign-in, navigate to the home page or dashboard
      alert("Successfully signed in!");
      navigate("/home");  // Replace with the appropriate route
    } else {
      alert("Please enter both email and password.");
    }
  }

  return (
    <Components.Container>
      <Components.SignUpContainer signinIn={signIn}>
        <Components.Form onSubmit={handleSubmit}>
          <Components.Title>Create Account</Components.Title>

          {/* Step 1: Name, Email, Password */}
          {step === 1 && (
            <>
              <Components.Input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
            </>
          )}

          {/* Step 2: Laptop Name, Handphone Name */}
          {step === 2 && (
            <>
              <Components.Input
                type="text"
                name="laptopName"
                placeholder="Laptop Name"
                value={formData.laptopName}
                onChange={handleInputChange}
                required
              />
              <Components.Input
                type="text"
                name="handphoneName"
                placeholder="Handphone Name"
                value={formData.handphoneName}
                onChange={handleInputChange}
                required
              />
            </>
          )}

          {/* Step 3: Document Upload */}
        {step === 3 && (
          <div
            style={{
              textAlign: "center", // Mengatur teks berada di tengah
              cursor: "pointer", // Mengubah kursor menjadi pointer
            }}
          >
            {/* Teks Instruksi di atas kotak */}
            <p style={{ marginBottom: "10px" }}>
              Drag and drop a document here, or click to upload
            </p>

            {/* Kotak Bergaris Putus-putus */}
            <div
              style={{
                border: "2px dashed #ccc", // Garis putus-putus
                width: "50px", // Menetapkan lebar kotak
                height: "50px", // Menetapkan tinggi kotak
                position: "relative", // Agar tanda tambah bisa diposisikan di dalam div
                margin: "0 auto", // Menjaga agar kotak berada di tengah
                overflow: "hidden", // Menyembunyikan konten yang melampaui kotak
                border: formData.documents ? "none" : "2px dashed #ccc", // Menghapus garis putus-putus jika sudah ada file
              }}
              onDrop={handleDrop} // Menangani drop file
              onDragOver={(e) => e.preventDefault()} // Mencegah perilaku default saat drag-over
            >
              {/* Jika ada file yang dipilih, tampilkan preview-nya */}
            {formData.documents ? (
              <img
                src={URL.createObjectURL(formData.documents)} // Menampilkan gambar yang dipilih
                alt="Preview"
                style={{
                  width: "100%", // Mengisi lebar kotak
                  height: "100%", // Mengisi tinggi kotak
                  objectFit: "cover", // Menjaga proporsi gambar agar tetap terpotong secara estetis
                }}
              />
            ) : (
              <div
                style={{
                  position: "absolute", // Agar tanda tambah bisa diposisikan dengan tepat
                  top: "50%", // Menempatkan tanda tambah di tengah secara vertikal
                  left: "50%", // Menempatkan tanda tambah di tengah secara horizontal
                  transform: "translate(-50%, -50%)", // Menyesuaikan posisi agar benar-benar di tengah
                  fontSize: "16px", // Ukuran tanda tambah yang lebih kecil agar sesuai dengan kotak
                  color: "#ccc", // Warna tanda tambah
                  cursor: "pointer"
                }}
              >
              <label htmlFor="file-upload" style={{ cursor: "pointer" }}>    
                +
              </label>
              </div>
            )}
            </div>

            {/* Input file untuk memilih file */}
            <input
              type="file"
              onChange={handleFileUpload}
              style={{ display: "none" }} // Menyembunyikan input file
              id="file-upload"
            />
            
            {/* Menampilkan file yang dipilih */}
            {formData.documents && (
              <p>Selected file: {formData.documents.name}</p>
            )}
          </div>
        )}



          {/* Navigation Buttons */}
          <div style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}>
            {step > 1 && (
              <div style={{ display: "flex", justifyContent: "center"}}>
              <Components.Button 
                type="button" 
                onClick={handlePrevious} 
                style={{ width: "auto", paddingLeft: "20px", paddingRight: "20px" }}
              >
                Previous
              </Components.Button>
            </div>
          )}
            <Components.Button type="submit" style={{ width: "auto", paddingLeft: "30px", paddingRight: "30px", marginLeft: "10px" }}>
              {step === 3 ? "Submit" : "Next"}
            </Components.Button>
          </div>

          {/* Progress Bar */}
          <div style={{ marginTop: "20px", width: "100%", backgroundColor: "#e0e0e0", borderRadius: "5px" }}>
            <div
              style={{
                width: `${progress}%`,
                height: "10px",
                backgroundColor: "#76c7c0",
                borderRadius: "5px",
                transition: "width 0.3s ease",
              }}
            ></div>
          </div>
        </Components.Form>
      </Components.SignUpContainer>

      {/* Rest of the code remains the same */}
      <Components.SignInContainer signinIn={signIn}>
        <Components.Form onSubmit={(e) => signInVerification(e, navigate)}>
          <Components.Title>Sign in</Components.Title>
          <Components.Input type="email" name="email" placeholder="Email" />
          <Components.Input
            type="password"
            name="password"
            placeholder="Password"
          />
          <Components.Anchor href="#">Forgot your password?</Components.Anchor>
          <Components.Button type="submit">Sign In</Components.Button>
        </Components.Form>
      </Components.SignInContainer>

      <Components.OverlayContainer signinIn={signIn}>
        <Components.Overlay signinIn={signIn}>
          <Components.LeftOverlayPanel signinIn={signIn}>
            <Components.Title>Welcome Back!</Components.Title>
            <Components.Paragraph>
              To keep connected with us please login with your personal info
            </Components.Paragraph>
            <Components.GhostButton onClick={() => toggle(true)}>
              Sign In
            </Components.GhostButton>
          </Components.LeftOverlayPanel>

          <Components.RightOverlayPanel signinIn={signIn}>
            <Components.Title>Hello, Friend!</Components.Title>
            <Components.Paragraph>
              Enter your personal details and start your journey with us
            </Components.Paragraph>
            <Components.GhostButton onClick={() => toggle(false)}>
              Sign Up
            </Components.GhostButton>
          </Components.RightOverlayPanel>
        </Components.Overlay>
      </Components.OverlayContainer>
    </Components.Container>
